export default [
  {
    key: 'inputMask',
    ignore: true
  },
  {
    key: 'allowMultipleMasks',
    ignore: true
  },
  {
    key: 'showWordCount',
    ignore: true
  },
  {
    key: 'showCharCount',
    ignore: true
  },
  {
    type: 'textfield',
    input: true,
    weight: 310,
    key: 'prefix',
    label: 'prefix',
    tooltip: 'Specify the prefix symbol after the component (e.g.: USD, EUR)'
  },
  {
    type: 'textfield',
