      create(EditTable, schema).then(edittable => {
        expect(edittable.getMeta()).to.deep.equal({ A: 1, B: 1, Header: 4 });
        done();
      }, done).catch(done);
    });
  });

  describe('setMeta', () => {
    it('should save row groups data to submission metadata', done => {
      const groups = [
        {
          label: 'A',
          numberOfRows: 1
        },
        {
          label: 'B',
          numberOfRows: 1
        },
        {
          label: 'Header',
