      ];

      const schema = Object.assign({}, basic, {
        rowGroups: [...groups]
      });

      create(EditTable, schema).then(edittable => {
        const metadata = edittable.getMeta();
        edittable.setMeta();
        expect(edittable.root._submission.metadata[schema.key]).to.deep.equal(metadata);
        done();
      }, done).catch(done);
    });
  });
});

